import React, { useState } from "react";
import "./NewGroup.css"

export default function Group() {
  const [members, setMembers] = useState([]);
  const [amount, setAmount] = useState(0);
  const button = document.getElementById('add-friends-button');
  const dropdown = document.getElementById('add-friends-dropdown');

  const addMember = (name) => {
    setMembers([...members, name]);
  };

  const handleChange = (event) => {
    setAmount(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Amount entered: ", amount);
  };

  /* const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Group Name </label>
          <input type="text" name="groupname" required />
          //{renderErrorMessage("err_email")}
        </div>
        <div className="input-container">
          <label>You paid </label>
          <input type="number" name="paid" required />
          //{renderErrorMessage("err_name")}
        </div
      </form>
    </div>
  );

  return (
    <div>
      <Header/>
      <div className="newgroup">
        <div className="newgroup-form">
          <div className="title">Create A New Group</div>
          //{isValidSignUp ? <div>User is successfully signed up</div> : renderForm}
        </div>
      </div>
    </div>
  ); */
  return (
    <center>
    <div>
      <h1>Create A New Group</h1>
      <button class="add-friends-button">Add Friends</button>
      <br />
      <form onSubmit={handleSubmit}>
        <label>
          Group name:
          <input type="text" onChange={(e) => addMember(e.target.value)} />
        </label>
        <br />
        <br />
        <br />
        <label>
          You paid:
          <input type="number" value={amount} onChange={handleChange} />
        </label>
        <br />
        <br />
        <input type="submit" value="Submit" />
      </form>
    </div>
    </center>
  );
}
